import React, { Component } from 'react';
import axios from 'axios';
import Chart from './Chart.js';
import CompanyTable from './CompanyTable.js';
import { LinkedinShareButton, WhatsappShareButton, TwitterShareButton} from 'react-share';
import { LinkedinIcon, WhatsappIcon, TwitterIcon } from 'react-share';

export default class CompanyDetails extends Component {
  constructor(props) {
    super(props)
    this.state = {
      company: {},
      diversityScore: {},
      dataFetched: false,
    }
  }

  getData = () => {
    axios
      .get(`/api/companies/${this.props.match.params.id}`)
      .then((response) => {
        // console.log(response.data);
        this.setState({
          company: response.data,
          dataFetched: true,
          // this unsets the flag when the data is available
          // dataRequested: false
        })
      })
      .catch((err) => {
        console.log(err)
        if (err.response.status === 404) {
          this.setState({
            error: 'Not found 🤷‍♀️🤷‍♂️',
          })
        }
      })
  }

  componentDidMount() {
    this.getData()
  }

  render() {
    if (this.state.dataFetched) {
      console.log('CompanyDetails', this.state.company)
      return (
        <>
          <div className='table-auto'>
            <h1>Name: {this.state.company.company_name}</h1>
            <Chart company={this.state.company} />
          </div>

          <div>
            <CompanyTable company={this.state.company} />
          </div>

          <div className = 'tw-flex tw-items-center tw-justify-center tw-relative tw-w-full'>
            <LinkedinShareButton
              url = {
                `http://findunity.herokuapp.com/`
                // `http://most-diverse-100/companies/${this.state.company._id}.herokuapp.com/`
              } 
               title = { `${this.state.company} is part of the most diverse 100 companies ranking of imagine foundation`}
              >
              <LinkedinIcon size={32} round={true} />
              </LinkedinShareButton>


              <WhatsappShareButton
              url = {
                `http://findunity.herokuapp.com/`
                // `http://most-diverse-100/companies/${this.state.company._id}.herokuapp.com/`
              } 
               title = { `${this.state.company} is part of the most diverse 100 companies ranking of imagine foundation`}
              >
              <WhatsappIcon size={32} round={true} />
              </WhatsappShareButton>

              <TwitterShareButton
              url = {
                `http://findunity.herokuapp.com/`
                // `http://most-diverse-100/companies/${this.state.company._id}.herokuapp.com/`
              } 
               title = {`${this.state.company} is part of the most diverse 100 companies ranking of imagine foundation`}
              >
              <TwitterIcon size={32} round={true} />
              </TwitterShareButton>

          </div>
        </>
      )
    } else {
      return null
    }
  }
}
