import React, { Component } from 'react';
import { Doughnut } from 'react-chartjs-2';

export default class Chart extends Component {

  constructor(props){
    super(props)
    this.state = {
      chartData:{
        labels: [
          "Diversity",
          "Opportunity",
          "Growth"
      ],
      datasets: [
          {
              data: [30, 50, 100],

              backgroundColor: [
                  "#FF6384",
                  "#36A2EB",
                  "#FFCE56"
              ],
              hoverBackgroundColor: [
                  "#FF6384",
                  "#36A2EB",
                  "#FFCE56"
              ]
          }]
      }
    }
  }

  render() {
    return (
      <div className="chart">
          <Doughnut
            data={this.state.chartData}
            width={100}
            height={50}
            options={{ maintainAspectRatio: false }}
          />

      </div>
    )
  }
}
