import React, { Component } from 'react';
import { Doughnut } from 'react-chartjs-2';

export default class Chart extends Component {

  constructor(props){
    super(props)
    this.state = {
      chartData:{
        labels: [
          "Diversity",
          "Opportunity",
          "Growth"
      ],
      datasets: [
          {
              data: [300, 50, 100],

              backgroundColor: [
                  "#FF6384",
                  "#36A2EB",
                  "#FFCE56"
              ],
              hoverBackgroundColor: [
                  "#FF6384",
                  "#36A2EB",
                  "#FFCE56"
              ]
          }]
      }
    }
  }
  render() {
    return (
      <div className="chart">
          <Doughnut
            data={this.state.chartData}
            width={10}
            height={50}
            options={{ rotation: 85 * Math.PI,
      circumference: 60 * Math.PI }}
          />

      </div>
    )
  }
}

